package class07;

public class WhileLoopDemo2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int b=100;
		while(b>=1) {

			System.out.println(b+" ");
			b--;
		}
		System.out.println("---------------------------------------------------");
		//Evein Number
		
		int i=1;
		while (i<=20) {
			if (i%2==0) {
				System.out.println(i);
			}
			i++;
		}

	}

}
